mapper.select:
SELECT m_name
  FROM sl_member
WHERE m_id = #{m_id}
AND m_passwd = #{m_passwd}

SELECT m_id
  FROM sl_member
WHERE m_email = #{m_email}
AND m_name = #{m_name}
AND m_birth_date = #{m_birth_date}

SELECT m_id
  FROM sl_member
WHERE m_tel = #{m_tel}
AND m_name = #{m_name}
AND m_birth_date = #{m_birth_date}

SELECT m_passwd
  FROM sl_member
WHERE m_email = #{m_email}
AND m_name = #{m_name}
AND m_id = #{m_id}
AND m_birth_date = #{m_birth_date}

SELECT m_passwd
FROM sl_member
WHERE m_tel = #{m_tel}
AND m_name = #{m_name} 
AND m_id = #{m_id}
AND m_birth_date = #{m_birth_date}
